import org.apache.spark.sql.types._
import java.sql.Date

// case class Movie(author:String, movie_id:Integer, movie_name:String, rating:Integer, review_id:Long, review_txt:String, source:String, date:Date)

//val movies = spark.read.json("hdfs://nn01/user/data/items-top50.jl") //전체 돌리기 전에 이 데이터로 테스트
val movies = spark.read.json("hdfs://nn01/user/data/items-all.jl") //실 데이터
    .withColumn("rating", 'rating.cast(IntegerType))
    .withColumn("review_id", 'review_id.cast(LongType))
    .withColumn("date", 'date.cast(DateType))
    // .as[Movie]

// movies.printSchema()
movies.createOrReplaceTempView("movies")
movies.show()
movies.write.parquet("/home/eduuser/data/movies.parquet")

%spark
/* Create text input form */
z.input("formName")

/* Create text input form with default value */
z.input("formName", "defaultValue")

/* Create select form */
z.select("formName", Seq(("option1", "option1DisplayName"),
                         ("option2", "option2DisplayName")))

/* Create select form with default value*/
z.select("formName", "option1", Seq(("option1", "option1DisplayName"),
                                    ("option2", "option2DisplayName")))

z.angularBind("run", 0)
var numWatched = 0
z.angularWatch("run", (before, after) => {
    numWatched = numWatched + 1
})
